import React, {useRef, useState} from "react";
import "./styles/App.css";
import PostList from "./component/PostList";
import MyButton from "./component/UI/button/MyButton";
import MyInput from "./component/UI/input/MyInput";

const App = () => {
    const [posts, setPosts] = useState([
        {id: 1, title: "JavaScript", body: "JavaScript - язык программирования."},
        {id: 2, title: "Java", body: "Java - язык программирования."},
        {id: 3, title: "Python", body: "Python - язык программирования."}
    ])
    const [post, setPost] = useState({
        title:'',
        body:''
    });


    const addNewPost = (e) => {
        e.preventDefault();
        setPosts([...posts, newPost]);
        setPosts({title:'', body:''});
    }

    return (
        <div className="App">
            <form>
                <MyInput value={post.title}
                         onChange={e=>setPosts({...post, title: e.target.value})}
                         type='text' placeholder="Название поста"/>
                <MyInput type='text'
                         value={post.body}
                         onChange={e=>setPosts({...post, body: e.target.value})}
                         placeholder="Описание поста"/>
                <MyButton onClick={addNewPost}>Создать пост</MyButton>
            </form>
            <PostList posts={posts} title="Список постов 1"/>
        </div>
    );
}

export default App;
