export const getPagesCount = (totalCount, limit) => {
    return Math.ceil(totalCount / limit);
}

export const  getPagesArray = (totalPages) => {
    let pagesArray = [];
    for (let i = 0; i < totalPages; i++) {
        pagesArray.push(i+1)
    }
}
